package com.mahanko.finalproject.model.dao.impl;

import com.mahanko.finalproject.exception.DaoException;
import com.mahanko.finalproject.model.entity.menu.Ingredient;
import org.testng.annotations.Test;

import java.util.List;

import static org.testng.Assert.*;

public class IngredientDaoImplTest extends AbstractDaoTest {
    private static final String ingredientName = "Ingredient";
    private static final long ingredientId = 1;
    private static final Ingredient ingredient = Ingredient
            .newBuilder()
            .setName(ingredientName)
            .setPicture("")
            .build();
    private final IngredientDaoImpl ingredientDao = IngredientDaoImpl.getInstance();

    @Test
    public void testInsert() throws DaoException {
        assertTrue(ingredientDao.insert(ingredient));
    }

    @Test(priority = 1)
    public void testExistsWithName() throws DaoException {
        assertTrue(ingredientDao.existsWithName(ingredientName));
    }

    @Test(priority = 1)
    public void testFindByName() throws DaoException {
        var actual = ingredientDao.findByName(ingredientName);
        assertEquals(actual, List.of(ingredient));
    }

    @Test(priority = 1)
    public void testFindAll() throws DaoException {
        var actual = ingredientDao.findAll();
        assertEquals(actual, List.of(ingredient));
    }

    @Test(priority = 1)
    public void testFindById() throws DaoException {
        var optionalIngredient = ingredientDao.findById(ingredientId);
        assertTrue(optionalIngredient.isPresent());
    }

    @Test(priority = 2, dependsOnMethods = "testFindById")
    public void testUpdate() throws DaoException {
        double newCalories = 20;
        ingredient.setCalories(newCalories);
        ingredientDao.update(ingredientId, ingredient);
        var updatedIngredient = ingredientDao.findById(ingredientId).get();
        assertEquals(updatedIngredient, ingredient);
    }

    @Test(priority = 2, dependsOnMethods = "testFindByName")
    public void testUpdateHidden() throws DaoException {
        ingredientDao.updateHidden(ingredientId, true);
        var actual = ingredientDao.findByName(ingredientName);
        assertEquals(actual, List.of());
    }

    @Test(priority = 2)
    public void testExistsMerge() throws DaoException {
        assertFalse(ingredientDao.existsMerge(ingredientId));
    }

    @Test(priority = 3, dependsOnMethods = "testFindById")
    public void testRemove() throws DaoException {
        ingredientDao.remove(ingredientId);
        var updatedIngredient = ingredientDao.findById(ingredientId);
        assertTrue(updatedIngredient.isEmpty());
    }
}